import React, { useState, useEffect } from "react";
import {
  Divider,
  FormControl,
  FormControlLabel,
  FormControlLabelText,
  Heading,
  Link,
  LinkText,
  Text,
  Input,
  InputField,
  VStack,
} from "@gluestack-ui/themed";
import { ActivityIndicator } from "react-native";

const Profile = ({ navigation }) => {
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");

  const bearerToken =
    "eyJhbGciOiJSUzI1NiIsImtpZCI6IjZDRjFFRTM0Qjc1MkY5QzUzMkJCODhGMTlDNDRBRkFFQTc5Mjc1M0YiLCJ4NXQiOiJiUEh1TkxkUy1jVXl1NGp4bkVTdnJxZVNkVDgiLCJ0eXAiOiJhdCtqd3QifQ..wA4CYGtDTQyVlwk9H54KmSWEyiuVJhfIHmukJXBByK9KOBbkv_WN_lCbAMvBstOED_yMNMQVIIidNH9QeafZtAPV58HNuFhWCgdCb7ISfA76ubjCOSlxeqegzMJi4Umdg-9kQgQxYoXxoMcXoLInysJJ3dZhR9ySwTLjmHYHmbhheWWDFRrQLrR_osAX5qOo9kg0wnEqnMg-IHncb02fSIX_k_0y_s2SRAPo8lSTK9R_bSOuE_pHQlBMo0lL_HMREkthzRu97JSmNFJiHc7n0ioc5QkPQnSaa1wU30l3wF8XP10xnBp9trEsyU5aKPd-ye2SX0enD2DqvKPU_29zGg";
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(
          `https://dev-api.waficommerce.com/api/account/my-profile`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${bearerToken}`,
            },
          }
        );

        // const response = await axios.get(
        //   "https://dev-api.waficommerce.com/api/account/my-profile",
        //   {
        //     headers: {
        //       accept: "*/*",
        //       Authorization: `Bearer ${bearerToken}`,
        //       "X-Requested-With": "XMLHttpRequest",
        //     },
        //   }
        // );
        setProfileData(response.data);
      } catch (error) {
        setErrorMessage("Failed to load profile data!");
        console.error("Error fetching profile data:", error.response);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  if (loading) {
    return <ActivityIndicator size="large" color="#0000ff" />;
  }

  if (errorMessage) {
    return <Text>{errorMessage}</Text>;
  }

  return (
    <VStack p="$4">
      <VStack>
        <Heading color="$text500">My Profile</Heading>

        {/* display username */}
        <Text>{profileData.userName}</Text>
        {/* <FormControl>
          <FormControlLabel>
            <FormControlLabelText>Username</FormControlLabelText>
          </FormControlLabel>
          <Input>
            <InputField value={profileData?.userName || ""} isReadOnly />
          </Input>
        </FormControl> */}

        {/* display email */}
        <FormControl>
          <FormControlLabel>
            <FormControlLabelText>Email</FormControlLabelText>
          </FormControlLabel>
          <Input>
            <InputField value={profileData?.email || ""} isReadOnly />
          </Input>
        </FormControl>

        {/* display phone number */}
        <FormControl>
          <FormControlLabel>
            <FormControlLabelText>Phone Number</FormControlLabelText>
          </FormControlLabel>
          <Input>
            <InputField value={profileData?.phoneNumber || ""} isReadOnly />
          </Input>
        </FormControl>
      </VStack>
      <Divider />
      <Link onPress={() => navigation.navigate("PasswordChange")}>
        <LinkText color="blue">Change Password</LinkText>
      </Link>
    </VStack>
  );
};

export default Profile;
