import React, { useState } from "react";
import {
  AlertCircleIcon,
  ButtonText,
  EyeIcon,
  FormControl,
  FormControlError,
  FormControlErrorIcon,
  FormControlErrorText,
  FormControlHelper,
  FormControlHelperText,
  FormControlLabel,
  FormControlLabelText,
  Heading,
  Input,
  InputField,
  InputSlot,
  Link,
  LinkText,
  Text,
  VStack,
} from "@gluestack-ui/themed";
import { EyeOffIcon } from "@gluestack-ui/themed";
import { Button } from "@gluestack-ui/themed";
import { InputIcon } from "@gluestack-ui/themed";
import axios from "axios";

const PasswordChange = ({ navigation }) => {
  const [showPassword, setShowPassword] = useState(false);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleState = () => {
    setShowPassword((showState) => !showState);
  };

  const handleChangePassword = async () => {
    if (newPassword !== confirmPassword) {
      setErrorMessage("New password and confirm password do not match!");
      return;
    }

    setIsLoading(true);
    setErrorMessage("");

    try {
      const response = await axios.post(
        "https://dev-api.waficommerce.com/api/account/my-profile/change-password",
        {
          currentPassword: currentPassword,
          newPassword: newPassword,
        },
        {
          headers: {
            "Content-Type": "application/json",
            // RequestVerificationToken: "your-verification-token", // Add verification token here
            "X-Requested-With": "XMLHttpRequest",
          },
        }
      );

      if (response.status === 200) {
        alert("Password changed successfully!");
        navigation.navigate("Profile");
      }
    } catch (error) {
      setErrorMessage("Failed to change password. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <VStack>
      {/* form for password change */}
      <FormControl
        p="$4"
        borderWidth="$1"
        borderRadius="$lg"
        borderColor="$borderLight300"
        $dark-borderWidth="$1"
        $dark-borderRadius="$lg"
        $dark-borderColor="$borderDark800"
      >
        <VStack space="xl">
          {/* heading */}
          <Heading color="$text500" lineHeight="$md">
            Change your current password
          </Heading>

          {/* current password field */}
          <FormControl
            size="md"
            isDisabled={false}
            isInvalid={false}
            isReadOnly={false}
            isRequired={true}
          >
            <FormControlLabel mb="$1">
              <FormControlLabelText>Old Password</FormControlLabelText>
            </FormControlLabel>
            <Input>
              <InputField
                type={showPassword ? "text" : "password"}
                value={currentPassword}
                onChangeText={(text) => setCurrentPassword(text)}
              />
              <InputSlot pr="$3" onPress={handleState}>
                <InputIcon
                  as={showPassword ? EyeIcon : EyeOffIcon}
                  color="$darkBlue500"
                />
              </InputSlot>
            </Input>
            <FormControlHelper>
              <FormControlHelperText>
                Must be at least 6 characters.
              </FormControlHelperText>
            </FormControlHelper>
          </FormControl>

          {/* new password field */}
          <FormControl
            size="md"
            isDisabled={false}
            isInvalid={false}
            isReadOnly={false}
            isRequired={true}
          >
            <FormControlLabel mb="$1">
              <FormControlLabelText>New Password</FormControlLabelText>
            </FormControlLabel>
            <Input>
              <InputField
                type={showPassword ? "text" : "password"}
                value={newPassword}
                onChangeText={(text) => setNewPassword(text)}
              />
              <InputSlot pr="$3" onPress={handleState}>
                <InputIcon
                  as={showPassword ? EyeIcon : EyeOffIcon}
                  color="$darkBlue500"
                />
              </InputSlot>
            </Input>
            <FormControlHelper>
              <FormControlHelperText>
                Must be at least 6 characters.
              </FormControlHelperText>
            </FormControlHelper>
          </FormControl>

          {/* confirm password */}
          <FormControl
            size="md"
            isDisabled={false}
            isInvalid={false}
            isReadOnly={false}
            isRequired={true}
          >
            <FormControlLabel mb="$1">
              <FormControlLabelText>Confirm New Password</FormControlLabelText>
            </FormControlLabel>
            <Input>
              <InputField
                type={showPassword ? "text" : "password"}
                value={confirmPassword}
                onChangeText={(text) => setConfirmPassword(text)}
              />
              <InputSlot pr="$3" onPress={handleState}>
                <InputIcon
                  as={showPassword ? EyeIcon : EyeOffIcon}
                  color="$darkBlue500"
                />
              </InputSlot>
            </Input>
            <FormControlHelper>
              <FormControlHelperText>
                Must be at least 6 characters.
              </FormControlHelperText>
            </FormControlHelper>
          </FormControl>

          {/* Error Message */}
          {errorMessage ? (
            <FormControlError>
              <FormControlErrorIcon as={AlertCircleIcon} />
              <FormControlErrorText>{errorMessage}</FormControlErrorText>
            </FormControlError>
          ) : null}

          {/* change password button */}
          <Button
            ml="auto"
            onPress={handleChangePassword}
            isLoading={isLoading}
          >
            <ButtonText color="$white">Change Password</ButtonText>
          </Button>
        </VStack>
      </FormControl>

      {/* footer */}
      <VStack p="$4">
        <Text>Don't want to change password?</Text>
        <Link onPress={() => navigation.navigate("Profile")}>
          <LinkText color="blue">Go back to profile</LinkText>
        </Link>
      </VStack>
    </VStack>
  );
};

export default PasswordChange;
